declare(strict_types=1);

namespace Mezzio\Authentication\OAuth2\Entity;

trait RevokableTrait
{
    /**
     * @var bool
     */
    protected $revoked;

    /**
     * @return bool
     */
    public function isRevoked(): bool
    {
        return $this->revoked;
    }

    /**
     * @param bool $revoked
     */
    public function setRevoked(bool $revoked): void
    {
        $this->revoked = $revoked;
    }
}
